mapper.select:
select * from ( select F_WORK_NO, F_TITLE, F_AUTHOR, F_YEAR, F_CONTEST, F_AWARD, F_MANAGER, F_HOST, F_REG_DATE, F_FILEPATH, ROW_NUMBER() over (order by F_WORK_NO desc) as rn from T_AWARD_WORK where F_STATUS = 'N'
AND TO_CHAR(F_REG_DATE, 'YYYY-MM-DD') >= #{staDate}
AND TO_CHAR(F_REG_DATE, 'YYYY-MM-DD') <= #{endDate}
AND F_YEAR >= #{staYear}
AND F_YEAR <= #{endYear}
AND F_TITLE like '%' || #{keyword} || '%'
AND F_AUTHOR like '%' || #{keyword} || '%'
AND F_HOST like '%' || #{keyword} || '%'
AND F_MANAGER like '%' || #{keyword} || '%'
AND (F_TITLE like '%' || #{keyword} || '%' OR F_AUTHOR like '%' || #{keyword} || '%' OR F_HOST like '%' || #{keyword} || '%' OR F_MANAGER like '%' || #{keyword} || '%')
) where rn between #{startRow} and #{endRow}

select count(*) from T_AWARD_WORK where F_STATUS = 'N'
AND TO_CHAR(F_REG_DATE, 'YYYY-MM-DD') >= #{staDate}
AND TO_CHAR(F_REG_DATE, 'YYYY-MM-DD') <= #{endDate}
AND F_YEAR >= #{staYear}
AND F_YEAR <= #{endYear}
AND F_TITLE like '%' || #{keyword} || '%'
AND F_AUTHOR like '%' || #{keyword} || '%'
AND F_HOST like '%' || #{keyword} || '%'
AND F_MANAGER like '%' || #{keyword} || '%'
AND (F_TITLE like '%' || #{keyword} || '%' OR F_AUTHOR like '%' || #{keyword} || '%' OR F_HOST like '%' || #{keyword} || '%' OR F_MANAGER like '%' || #{keyword} || '%')

select * from ( select F_WORK_NO, F_FILEPATH, F_TITLE, F_AUTHOR, F_CONTEST, F_AWARD, F_HOST, F_MANAGER, F_YEAR, F_REG_DATE, ROW_NUMBER() over (order by F_WORK_NO desc) as rn from T_AWARD_WORK where F_STATUS = 'N'
AND TO_CHAR(F_REG_DATE, 'YYYY-MM-DD') >= #{staDate}
AND TO_CHAR(F_REG_DATE, 'YYYY-MM-DD') <= #{endDate}
AND F_YEAR >= #{staYear}
AND F_YEAR <= #{endYear}
AND F_TITLE like '%' || #{keyword} || '%'
AND F_AUTHOR like '%' || #{keyword} || '%'
AND F_HOST like '%' || #{keyword} || '%'
AND F_MANAGER like '%' || #{keyword} || '%'
AND (F_TITLE like '%' || #{keyword} || '%' OR F_AUTHOR like '%' || #{keyword} || '%' OR F_HOST like '%' || #{keyword} || '%' OR F_MANAGER like '%' || #{keyword} || '%')
)
